import React, { Component } from 'react'
import Cookies from 'universal-cookie'
import axios from 'axios'
import './Feed.css'
import Post from '../../Components/Post/Post'

const cookies = new Cookies()

class Feed extends Component {
    constructor() {
        super()
        this.state = {
            posts: [
                { id: 1, user: 'testuser', body: 'This is my first post', 'imgur_link': 'https://www.publicdomainpictures.net/pictures/50000/velka/ny-cityscape.jpg', 'category': 'category' },
                { id: 2, user: 'testuser2', body: 'Another Post', 'imgur_link': 'https://www.publicdomainpictures.net/pictures/50000/velka/ny-cityscape.jpg', 'category': 'category' },
                { id: 3, user: 'testuser2', body: 'Post 3', 'imgur_link': 'https://www.publicdomainpictures.net/pictures/50000/velka/ny-cityscape.jpg', 'category': 'category' },
                { id: 4, user: 'testuser3', body: '', 'imgur_link': 'https://www.publicdomainpictures.net/pictures/50000/velka/ny-cityscape.jpg', 'category': 'category' }

            ]
        }
    }
    componentWillMount() {
        if (!cookies.get('user')) window.location = '/login'
        let user = cookies.get('user')
    }
    render() {
        return (
            <div className="Feed">
                <div className="PostsView">
                    {
                        this.state.posts.map(post => {
                            return (
                                <Post key={post.id} postID={`post_${post.id}`} user={post.user} body={post.body} imgurLink={post.imgur_link} category={post.category} />
                            )
                        })
                    }
                </div>
            </div>
        )
    }
}

export default Feed
