import React, { Component } from 'react'
import Cookies from 'universal-cookie'
import axios from 'axios'
import './Category.css'
import Post from '../../Components/Post/Post'

const cookies = new Cookies()

class Category extends Component {
    constructor() {
        super()
        this.state = {
            category: 'Category',
            posts: [],
            postCount: '',
            sort: 'newest'
        }
        this.sortChange = this.sortChange.bind(this)
    }

    componentWillMount() {
        if (!cookies.get('user')) window.location = '/login'
        let token = cookies.get('token')
        let path = this.props.location.pathname
        if (path == '/category' || path == '/category/') alert('no category provides')
        let category = path.split('/category/')[1]
        this.setState({ category })
        axios.get(`/api/post/category/${category}?token=${token}`)
            .catch(err => console.error(err))
            .then(response => {
                let posts = response.data.result
                let postCount = `${posts.length} ${posts.length == 1 ? 'Post' : 'Posts'}`
                this.setState({ posts, postCount })
            })
    }

    sortChange(e) {
        this.setState({ sort: e.target.value })
    }

    render() {
        return (
            <div className="Category">
                <div className="header">
                    <h1>{this.state.category}</h1>
                    <h4>{this.state.postCount}</h4>
                    <select name="sort" id="sort-select" onChange={this.sortChange} value={this.state.sort}>
                        <option value="newest">Newest first</option>
                        <option value="oldest">Oldest first</option>
                    </select>
                </div>

                <div className="PostsView">
                    {this.state.posts.map(post => (
                        <Post key={post.id} postID={`post_${post.id}`} user={post.user} body={post.description} imgurLink={post.imgur_link} category={post.category} />
                    ))}
                </div>

            </div>
        )
    }
}


export default Category
