import React, { Component } from 'react'
import Cookies from 'universal-cookie'
import axios from 'axios'
import './Feed.css'
import Post from '../../Components/Post/Post'

const cookies = new Cookies()

class Feed extends Component {
    constructor() {
        super()
        this.state = {
            posts: []
        }
    }
    componentWillMount() {
        if (!cookies.get('user')) window.location = '/login'
        let token = cookies.get('token')
        let follower = cookies.get('user').username
        axios.get(`/api/user/follow/${follower}?token=${token}`)
            .catch(err => console.error(err))
            .then(response => {
                console.log(response)
            })
    }
    render() {
        return (
            <div className="Feed">
                <div className="PostsView">
                    {
                        this.state.posts.map(post => {
                            return (
                                <Post key={post.id} postID={`post_${post.id}`} user={post.user} body={post.body} imgurLink={post.imgur_link} category={post.category} />
                            )
                        })
                    }
                </div>
            </div>
        )
    }
}

export default Feed
