import React, { Component } from 'react'
import './Post.css'
import Cookies from 'universal-cookie'
import { Link } from 'react-router-dom'
import axios from 'axios';

const cookies = new Cookies()
const token = cookies.get('token')

class Post extends Component {
    constructor(props) {
        super(props)
        this.state = { votes: 0 }
        this.clickImage = this.clickImage.bind(this)
        this.componentDidMount = this.componentDidMount.bind(this)
        this.upvote
        this.downvote = this.downvote.bind(this)
    }

    componentDidMount = () => {
        let that = this
        let postID = Number(this.props.postID.slice(-1))
        axios.get(`/api/vote/${postID}?token=${token}`)
            .then(res => {
                let payloadSum = 0
                res.data.result.map(vote => payloadSum += vote.payload)
                this.setState({ votes: payloadSum })
            })
            .catch(err => console.error(err))

        setTimeout(function () {
            if (that.props.body == '') {
                let thisID = that.props.postID
                let thisElelemt = document.getElementById(thisID)
                thisElelemt.style.cssText = 'height:auto !important'
            }
        }, 100)
    }

    clickImage() {
        let url = this.props.imgurLink
        let win = window.open(url, '_blank')
        win.focus()
    }

    upvote(e) {
        alert('upvote')
        e.target.classList.add('active')
        let altBtn = e.target.parent
        console.log('parent', altBtn)
    }

    downvote(e) {
        e.target.classList.add('active')
        let altBtn = e.target.parent
        console.log('parent', altBtn)
    }

    render() {
        return (
            <div className="Post" id={this.props.postID}>
                <img src={this.props.imgurLink} onClick={this.clickImage} />
                <p className="bodyText">{this.props.body}</p>
                <div className="blank"></div>
                <div className="voteButtons">
                    <button type="button" class="btn vote-button upvote-button" click={this.upvote.bind(this)}>U</button>
                    <button type="button" class="btn vote-button downvote-button" click={this.downvote}>D</button>
                </div >
                <div className="footer">
                    <p id="votes-text">{this.state.votes} points</p>
                    <span style={{ display: 'flex' }}>
                        <Link to={`/user/${this.props.user}`}><p id="user-text">{this.props.user}</p></Link>
                        <p id="category-text">in <Link id="category-link" to={`/c/${this.props.category}`}>{this.props.category}</Link></p>
                    </span>
                    <div className="blank"></div>
                    <div id="timestamp">4h ago</div>
                </div >
            </div >
        )
    }
}


export default Post
